import UIKit

class DetailWorker {
    let phoneStore = PhoneStore()

    func getPhoneDetail(withId id: Int) -> Phone? {
        return phoneStore.getPhoneDetail(withId: id)
    }

    func fetchPhoneImages(withId id: Int, completion: @escaping (Result<[PhoneImage]>) -> Void) {
        Network.shared.request(router: Router.getMobileImages(mobileId: "\(id)")) { (result: Result<[PhoneImage]>) in
            completion(result)
        }
    }
}
