import UIKit

enum List {
  // MARK: Use cases
  
  enum DeviceList {
    struct Request {
        init (withPredicate predicate: PhoneStore.Predicate = PhoneStore().getSortPredicate()?.predicate ?? .priceAscending) {
            self.fetchPridicate = predicate
        }
        let fetchPridicate: PhoneStore.Predicate
    }

    struct Response {
        var phoneList: [Phone]?
        var errorMessage: String?
    }

    struct Error {
        let errorMessage: String
    }

    struct ViewModel {
        struct DisplayPhone: Equatable {
            let id: Int
            let name: String
            let description: String
            let price: String
            let rating: String
            let thumbnailPath: String
            let isFavorite: Bool
        }
        var displayPhone: [DisplayPhone]
    }
  }
}
