    // MARK: Navigation
    func navigateToDetail() {
        let destinationVC = DetailViewController()
        var destinationDS = destinationVC.router?.dataStore
        passDataToDetailVC(source: dataStore!, destination: &destinationDS!)
        viewController?.show(destinationVC, sender: nil)
    }

    // MARK: Passing data
    func passDataToDetailVC(source: ListDataStore, destination: inout DetailDataStore) {
        if let selectedRow = viewController?.listTableView.indexPathForSelectedRow?.row {
            destination.phoneId = source.phoneList[selectedRow].id
        }
    }
}
