import UIKit

class ListWorker {
  func fetchPhoneList(completion: @escaping (Result<[Phone]>) -> Void) {
    Network.shared.request(router: Router.getMobilesList) { (result: Result<[Phone]>) in
        completion(result)
    }
  }
}
